import { Link } from "react-router-dom";
import { WishlistContext } from "../components/WishlistContext";
import React, { useContext } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar, faHeart } from "@fortawesome/free-solid-svg-icons";
import { CartContext } from "../reducers/CartContext"; 


const Product = (props) => {
    const { id, image, title, rating, price } =props.data;
    const { wishlist, setWishlist } = useContext(WishlistContext);
    const handleWishlistClick = (productId) => {
        setWishlist((prevState) => ({
          ...prevState,
          [productId]: !prevState[productId], // Toggle the wishlist status for the specific product
        }));
      };


      const { cartState, dispatch } = useContext(CartContext); // Get cartState and dispatch from context

      const handleAddToCart = (productId) => {
        // Logic to add product to cart
        dispatch({ type: "ADD_TO_CART", payload: productId });
      };

    return (
        <div className="col-sm-4" style={{width:"20px",height:"20px",gap:"20px"}}>
          <ul className="card" >
            <img src={image} className="img card-top-image" alt="..."  />

            <div className="wishlist">
              <FontAwesomeIcon
                icon={faHeart}
                className={`wishlist-icon ${wishlist[id] ? "wishlist-added" : ""}`}
                onClick={() => handleWishlistClick(id)}
              />
            </div>
            <div className="card-body">
              <h5 className="card-title">
                {title.length > 20 ? `${title.substring(0, 20)}...` : title}
              </h5>
              <div className="rating">
                {Array.from({ length: 5 }, (_, i) => (
                  <FontAwesomeIcon
                    key={i}
                    icon={faStar}
                    className={i < rating.rate ? "filled-star" : "empty-star"}
                  />
                ))}
                {rating.rate}
                <span>({rating.count})</span>
              </div>
              <h2>
                <span>&#36;</span>
                {price}
                <span style={{ fontSize: "22px", marginLeft: "10px", color: "#888" }}></span>
              </h2>

              <Link to={'/products/'+id} className="btn btn-primary"
              onClick={() => handleAddToCart(id)}>
              Add To Cart
              </Link> 
            </div>
          </ul>
        </div>
    )
}

export default Product;
