import { ActionTypes } from "../constants/action-types"

const initialState = {
    numberCart: 0,
    Carts: [],
    productData: [],
}

export const cartReducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case ActionTypes.GET_NUMBER_CART:
            return {
                ...state
            };
        case ActionTypes.ADD_TO_CART:
            // Check if the product is already in the cart
            const existingItem = state.Carts.find(item => item._id === payload._id);

            if (existingItem) {
                // If the product exists, update its quantity using forEach
                state.Carts.forEach(item => {
                    if (item._id === payload._id) {
                        item.quantity++;
                    }
                });
                return {
                    ...state,
                    numberCart: state.numberCart + 1,
                };
            } else {
                // If the product is not in the cart, add it
                const newItem = {
                    ...payload,
                    quantity: 1
                };
               console.log(...payload)
                return {
                    ...state,
                    Carts: [...state.Carts, newItem],
                    numberCart: state.numberCart + 1,
                };

            }

        case ActionTypes.FETCH_PRODUCT_DATA_SUCCESS:
            return {
                ...state,
                productData: payload,
            };

        default:
            return state;
    }
};
