import React,{useState} from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router";

const Wishlist = (productId) => {
  const wishlist = useSelector((state) => state.wishlist);
console.log(wishlist)
const [showWishlist, setShowWishlist] = useState(false); // State to toggle wishlist display

  // Function to toggle the wishlist display
  const handleShowWishlist = () => {
    setShowWishlist((prevShow) => !prevShow);
  };
  return (
    <div>
      <h2>Wishlist</h2>
      <ul>
        {Object.keys(showWishlist).map((productId) => (
          <li key={productId}>{wishlist[productId].title}</li>

        ))}
      </ul>
    </div>
  );
};

export default Wishlist;
