import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar, faHeart } from '@fortawesome/free-solid-svg-icons';
import Constants from '../apis/Constants';
import { useParams } from 'react-router';
import axios from 'axios';

const ProductList = () => {
    const{id,title,price,description,category,image,rating:[rate,count]}=useParams();
    const [products,setProducts]=useState([]);

    const getData=()=>{
        axios.get(Constants.BASE_URL + id)
        .then((response) => {
            console.log(response.data.data);
            setProducts(response.data.data);
        })
            .catch(error => console.log(error))
    }
    useEffect(() => {
        getData()
    }, [id])

    return (
        <>
           <div className="col-sm-4">
                <ul className="card" style={{ width: "18rem" }}>
               {
products.map((product, index)=>(
    <img src={image} className="img card-top-image"  alt="..."/>
) )
               }


                    <div className="wishlist">
                        <FontAwesomeIcon icon={faHeart} className="wishlist-icon"/>
                    </div>
                    <div className="card-body">
                        <h5 className="card-title">{title}</h5>
                        <div className="rating">
                            <FontAwesomeIcon icon={faStar} />
                            <FontAwesomeIcon icon={faStar} />
                            <FontAwesomeIcon icon={faStar} />
                            <FontAwesomeIcon icon={faStar} />
                            <FontAwesomeIcon icon={faStar} />
                        </div>
                        <h2>
                        <span>&#8377;</span>{price}
                        <span style={{fontsize:'22px',marginleft:'10px',color:'#888'}}>
                        </span>
                    </h2>

                        <a href="#" className="btn btn-primary">Add To Cart</a>
                    </div>
                </ul>
            </div>

        </>
    )
}

export default ProductList
