import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import HeaderCategory from "./components/HeaderCategory";
import ProductOrderSummary from "./pages/ProductOrderSummary";
import HomePage from "./pages/HomePage";
import ErrorPage from "./pages/ErrorPage";
import SignUPPage from "./pages/SignUPPage";
import LoginPage from "./pages/LoginPage";
import { WishlistProvider } from "../src/components/WishlistContext";

const App = () => {
  return (
    <Router>
      <WishlistProvider>
        <Routes>
          <Route path="/products/:id" element={<ProductOrderSummary />} />
          <Route path="/products/:category" element={HeaderCategory} />
          <Route path="/" element={<HomePage />} />
          <Route path="/signup" element={<SignUPPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="*" element={<ErrorPage />} />
        </Routes>
      </WishlistProvider>
    </Router>
  );
};

export default App;
