import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar, faHeart } from '@fortawesome/free-solid-svg-icons';
import Constants from '../apis/Constants';
import axios from 'axios';

const ProductList = () => {
    const MAX_TITLE_LENGTH = 20
    const [products, setProducts] = useState([]);
    const [wishlist, setWishlist] = useState({});
    const MAX_IMAGE_WIDTH = 200;
    const MAX_IMAGE_HEIGHT = 200;

    const getData = () => {
        axios
            .get(Constants.BASE_URL)
            .then((response) => {
                console.log(response.data);
                setProducts(response.data); // Set the products state with the API response data
            })
            .catch(error => console.log(error));
    };

    useEffect(() => {
        getData();
    }, []);

    const handleWishlistClick = (productId) => {
        setWishlist((prevState) => ({
            ...prevState,
            [productId]: !prevState[productId] // Toggle the wishlist status for the specific product
        }));
    };

    // Save the wishlist status to local storage when the wishlist state changes
    useEffect(() => {
        localStorage.setItem('wishlist', JSON.stringify(wishlist));
    }, [wishlist]);



    return (
        <div className="row">
            {products.map((product, index) => (
                <div className="col-sm-3" key={index}>
                    <ul className="card">
                        <img s src={product.image}
                            className="img card-top-image"
                            alt="..."
                            style={{
                                maxWidth: MAX_IMAGE_WIDTH,
                                maxHeight: MAX_IMAGE_HEIGHT,
                            }} />

                        <div className="wishlist">
                            <FontAwesomeIcon
                                icon={faHeart}
                                className={`wishlist-icon ${wishlist[product.id] ? 'wishlist-added' : ''}`}
                                onClick={() => handleWishlistClick(product.id)}
                            />
                        </div>
                        <div className="card-body">
                            <h5 className="card-title"> {product.title.length > MAX_TITLE_LENGTH
                                ? `${product.title.substring(0, MAX_TITLE_LENGTH)}...`
                                : product.title
                            }</h5>
                            <div className="rating">
                                {Array.from({ length: 5 }, (_, i) => (
                                    <FontAwesomeIcon
                                        key={i}
                                        icon={faStar}
                                        className={i < (product.rating.rate) ? 'filled-star' : 'empty-star'}
                                    />
                                ))}
                                {product.rating.rate}
                                <span>`({product.rating.count})`</span>
                            </div>
                            <h2>
                                <span>&#36;</span>{product.price}
                                <span style={{ fontSize: '22px', marginLeft: '10px', color: '#888' }}>
                                </span>
                            </h2>

                            <a href="#" className="btn btn-primary">Add To Cart</a>
                        </div>
                    </ul>
                </div>
            ))}
        </div>
    );
};

export default ProductList;
