import { WishlistContext } from "../components/WishlistContext";
import React, { useContext,useEffect,useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar, faHeart } from "@fortawesome/free-solid-svg-icons";
import { Link,useParams } from "react-router-dom";
import { CartContext } from "../reducers/CartContext"; 
import Constants from "../apis/Constants";
import axios from "axios";
import Endpoints from "../apis/Endpoints";

const ProductsFiltered = () => {
  const { wishlist, setWishlist } = useContext(WishlistContext);
  const { category } = useParams();
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    // Fetch products for the selected category from the API
    axios
      .get(Endpoints.CATEGORY_URL + category)
      .then((response) => {
        setFilteredProducts(response.data);
      })
      .catch((error) => console.log(error));
  }, [category]);

  const handleWishlistClick = (productId) => {
    setWishlist((prevState) => ({
      ...prevState,
      [productId]: !prevState[productId], // Toggle the wishlist status for the specific product
    }));
  };

  const { cartState, dispatch } = useContext(CartContext); // Get cartState and dispatch from context

  const handleAddToCart = (productId) => {
    // Logic to add product to cart
    dispatch({ type: "ADD_TO_CART", payload: productId });
  };

  return (
    <div className="row" style={{ marginTop: "40px", marginLeft: "2em", marginRight: "2em" }}>
      {filteredProducts.map((product) => (
        <div className="col-sm-3" key={product.id}>
          <ul className="card">
            <img src={product.image} className="img card-top-image" alt="..." style={{ maxWidth: "200px", maxHeight: "200px" }} />

            <div className="wishlist">
              <FontAwesomeIcon
                icon={faHeart}
                className={`wishlist-icon ${wishlist[product.id] ? "wishlist-added" : ""}`}
                onClick={() => handleWishlistClick(product.id)}
              />
            </div>
            <div className="card-body">
              <h5 className="card-title">
                {product.title.length > 20 ? `${product.title.substring(0, 20)}...` : product.title}
              </h5>
              <div className="rating">
                {Array.from({ length: 5 }, (_, i) => (
                  <FontAwesomeIcon
                    key={i}
                    icon={faStar}
                    className={i < product.rating.rate ? "filled-star" : "empty-star"}
                  />
                ))}
                {product.rating.rate}
                <span>({product.rating.count})</span>
              </div>
              <h2>
                <span>&#36;</span>
                {product.price}
                <span style={{ fontSize: "22px", marginLeft: "10px", color: "#888" }}></span>
              </h2>

              <Link to={'/products/'+product.id} className="btn btn-primary"
              onClick={() => handleAddToCart(product.id)}>
              Add To Cart
              </Link> 
            </div>
          </ul>
        </div>
      ))}
    </div>
  );
};

export default ProductsFiltered;
