import React, { useState, useEffect } from "react";
import axios from "axios";
import Constants from "../apis/Constants";
import ProductList from "../components/ProductsFiltered";

const HeaderCategory = () => {
  const [categories, setCategories] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    axios
      .get(Constants.BASE_URL)
      .then((response) => {
        console.log(response.data);
        setCategories([...new Set(response.data.map((product) => product.category))]);
        setFilteredProducts(response.data);
      })
      .catch((error) => console.log(error));
  }, []);

  const handleFilterProducts = (category) => {
    setSelectedCategory(category);
    if (category) {
      const filtered = filteredProducts.filter((product) => product.category === category);
      setFilteredProducts(filtered);
    } else {
      // If no category is selected, show all products
      setFilteredProducts(filteredProducts);
    }
  };

  return (
    <div>
      <ul className="nav justify-content-center">
        <li className="list-group-item" style={{ border: "none", cursor: "pointer" }} onClick={() => handleFilterProducts(null)}>
          All
        </li>
        {categories.map((category, index) => (
          <li
            key={index}
            className="list-group-item"
            style={{ border: "none", cursor: "pointer" }}
            onClick={() => handleFilterProducts(category)}
          >
            {category}
          </li>
        ))}
      </ul>

      <ProductList products={filteredProducts} />
    </div>
  );
};

export default HeaderCategory;
