const initialState = {};

export const wishListReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_TO_WISHLIST':
      const productToAdd = state.find((product) => product.id === action.payload);
      if (!productToAdd) {
        return state; // If product not found, return current state
      }
      return {
        ...state,
        [action.payload]: {
          ...productToAdd,
          wishlisted: true,
        },
      };
    case 'REMOVE_FROM_WISHLIST':
      const newState = { ...state };
      delete newState[action.productId];
      return newState;
    default:
      return state;
  }
};
