import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addToWishlist, removeFromWishlist } from '../Redux/actions/wishlist-actions';

const Wishlist = () => {
  const wishlist = useSelector((state) => state.wishlist);
  const dispatch = useDispatch();

  const handleWishlistClick = (productId) => {
    if (wishlist[productId]) {
      dispatch(removeFromWishlist(productId));
    } else {
      dispatch(addToWishlist(productId));
    }
  };

  return (
    <div>
      <h2>My Wishlist</h2>
      <ul>
        {Object.keys(wishlist).map((productId) => (
          <li key={productId}>
            {/* Display product information */}
            <button onClick={() => handleWishlistClick(productId)}>
              {wishlist[productId] ? 'Remove from Wishlist' : 'Add to Wishlist'}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Wishlist;
