import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router";
import Constants from "../apis/Constants";

const ProductOrderSummary=()=>{
  const {id} = useParams();
    const [product, setProduct] = useState({});

    const getData = () => {
        axios.get(Constants.BASE_URL+id)
            .then((response) => {
                console.log(response.data.data);
                setProduct(response.data.data);
            })
            .catch(error => console.log(error))
    };
    useEffect(() => {
        getData()
    }, [id])
    return(
        <>
        <h1>Page to show product details</h1>
        <div className="container">
                <div style={{ backgroundColor: "#fff", padding: "40px", marginTop: "80px", borderRadius: "10px", }}>
                    <div className="row">
                        <div className="col-md-6">
                            <img src={product.image} alt="" className="img-fluid" />
                        </div>
                        <div className="col-md-6">
                            <h5>{product.productName}</h5>
                            <p>{product.unit}</p>
                            <p>{product.description}</p>
                            <h2>
                                <span>&#8377;</span>{product.price}
                                <span style={{ fontsize: '22px', marginleft: '10px', color: '#888' }}>
                                    <del><span>&#8377;</span>{product.mrp}</del>
                                </span>
                            </h2>

                        </div>
                    </div>
                </div>
            </div>
        </>
    )
  }

  export default ProductOrderSummary
