import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Endpoints from "../apis/Endpoints";

const HeaderCategory = ({ products, setFilteredProducts }) => {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [selectedSortOption, setSelectedSortOption] = useState("default");

  useEffect(() => {
    axios
      .get(Endpoints.CATEGORIES_URL)
      .then((response) => {
        setCategories(response.data);
      })
      .catch((error) => console.log(error));
  }, []);

  function capitalizeFirstLetter(str) {
    return str.charAt(0).toUpperCase() + str.slice(1);
  }

  useEffect(() => {
    // Apply the filter based on the selected category
    if (selectedCategory === "All") {
      setFilteredProducts(products); // Show all products
    } else {
      const filteredProducts = products.filter(
        (product) => product.category === selectedCategory
      );
      setFilteredProducts(filteredProducts);
    }
  }, [selectedCategory, products, setFilteredProducts]);

  useEffect(() => {
    // Apply the sort option to the filtered products
    const sortedProducts = [...filteredProducts]; // Assuming you already have the filtered products in the state
    if (selectedSortOption === "name_asc") {
      sortedProducts.sort((a, b) => a.name.localeCompare(b.name));
    } else if (selectedSortOption === "name_desc") {
      sortedProducts.sort((a, b) => b.name.localeCompare(a.name));
    } else if (selectedSortOption === "price_asc") {
      sortedProducts.sort((a, b) => a.price - b.price);
    } else if (selectedSortOption === "price_desc") {
      sortedProducts.sort((a, b) => b.price - a.price);
    }

    setFilteredProducts(sortedProducts);
  }, [selectedSortOption, filteredProducts, setFilteredProducts]);

  return (
    <div>
      <ul className="nav justify-content-center">
        <li>
          <Link
            to="/"
            className={`list-group-item ${
              selectedCategory === "All" ? "active" : ""
            }`}
            style={{ border: "none", cursor: "pointer" }}
            onClick={() => setSelectedCategory("All")}
          >
            All
          </Link>
        </li>

        {categories.map((category, index) => (
          <li
            key={index}
            className={`list-group-item ${
              selectedCategory === category ? "active" : ""
            }`}
            style={{ border: "none", cursor: "pointer" }}
          >
            <Link
              to={`/products/category/${category}`}
              onClick={() => setSelectedCategory(category)}
            >
              {capitalizeFirstLetter(category)}
            </Link>
          </li>
        ))}
      </ul>

      {/* Sort options dropdown */}
      <label htmlFor="sort">Sort By:</label>
      <select
        id="sort"
        value={selectedSortOption}
        onChange={(e) => setSelectedSortOption(e.target.value)}
      >
        <option value="default">Default</option>
        <option value="name_asc">Name (A-Z)</option>
        <option value="name_desc">Name (Z-A)</option>
        <option value="price_asc">Price (Low to High)</option>
        <option value="price_desc">Price (High to Low)</option>
      </select>
    </div>
  );
};

export default HeaderCategory;
