import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router";
import Constants from "../apis/Constants";
import Endpoints from "../apis/Endpoints";
import Navbar from "../components/Navbar";

const ProductOrderSummary=()=>{
  const {id} = useParams();
  console.log(id)
  console.log(Endpoints.PRODUCTS_URL+id)

  const [product, setProduct] = useState({})


    const getData = () => {
        axios.get(Constants.BASE_URL+id)
            .then((response) => {
                console.log(response.data);
                setProduct(response.data);
            })
            .catch(error => console.log(error))
    };
    useEffect(() => {
      getData()
  }, [id])
    return(
        <>
        <Navbar />
        <div className="container">
                <div style={{ backgroundColor: "#fff", padding: "40px", marginTop: "80px", borderRadius: "10px" }}>
                    <div className="row">
                        <div className="col-md-4">

                            <img src={product.image} alt="" className="img-fluid" />
                        </div>
                        <div className="col-md-4" >
                            <h5>{product.title}</h5>

                            <p>{product.description}</p>
                            <h2>
                                <span>&#8377;</span>{product.price}
                                <span style={{ fontsize: '22px', marginleft: '10px', color: '#888' }}>

                                </span>
                            </h2> 
                       </div>
                       <div className="col-md-4">
                        <h5>OrderSummary</h5>
                       </div>
                    </div>
                </div>
            </div>
        </>
    )
  }

  export default ProductOrderSummary
