import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { addToWishlist, removeFromWishlist } from '../Redux/actions/wishlist-actions';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar, faHeart } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { addToCart } from '../Redux/actions/cart-actions';

const Wishlist = () => {
  const wishlist = useSelector((state) => state.wishlist);
  const dispatch = useDispatch();
  const [products, setProducts] = useState({});
  const handleWishlistClick = (productId) => {
    if (wishlist[productId]) {
      dispatch(removeFromWishlist(productId));
    } else {
      dispatch(addToWishlist(productId));
    }
  };

  useEffect(() => {
    // Fetch product details for all product IDs in the wishlist
    const productIds = Object.keys(wishlist);
    if (productIds.length > 0) {
      axios.all(productIds.map(productId => axios.get(`/api/products/${productId}`)))
        .then(axios.spread((...responses) => {
          const fetchedProducts = responses.reduce((acc, response) => {
            const product = response.data;
            acc[product.id] = product;
            return acc;
          }, {});
          setProducts(fetchedProducts);
        }))
        .catch(error => {
          console.error('Error fetching product details:', error);
        });
    }
  }, [wishlist]);

  const addToCartHandler = (productId) => {
    dispatch(addToCart(productId));
  };


  return (
    <div>
      <h2>My Wishlist</h2>

        {Object.keys(wishlist).map((productId) => (
          <div className="col-sm-3" key={productId} >
            {/* Display product information */}

            <ul className="card" style={{ padding: "0px", width: "300px", height: "500px", gap: "10px" }}>
              <img src={product.image} className="img card-top-image" alt="..." style={{ maxWidth: "200px", maxHeight: "200px", marginTop: "20px" }} />

              <div className="wishlist">
                <FontAwesomeIcon
                  icon={faHeart}
                  className={`wishlist-icon ${productWishlistStatus[product.id] ? "wishlist-added" : ""}`}
                  onClick={() => handleWishlistClick(productId)}



                  style={{ color: productWishlistStatus[product.id] ? "pink" : "grey" }}
                />

              </div>
              <hr />
              <div className="card-body">
                <h5 className="card-title">
                  {product.title.length > 20 ? `${product.title.substring(0, 20)}...` : product.title}
                </h5>
                <div className="rating">
                  {Array.from({ length: 5 }, (_, i) => (
                    <FontAwesomeIcon
                      key={i}
                      icon={faStar}
                      className={i < product.rating.rate ? "filled-star" : "empty-star"}
                    />
                  ))}
                  {product.rating.rate}
                  <span>({product.rating.count})</span>
                </div>
                <h2>
                  <span>&#36;</span>
                  {product.price}
                  <span style={{ fontSize: "22px", marginLeft: "10px", color: "#888" }}></span>
                </h2>
                <Link
                  to={'/products/' + product.id}
                  className="btn btn-primary"
                  style={{ width: "250px", height: "40px" }}

                >
                  ProductDetails
                </Link>
                <p></p>
                <Link

                  className="btn btn-primary"
                  style={{ width: "250px", height: "40px" }}
                  onClick={() => addToCartHandler(product.id)}
                ><i className="fas fa-shopping-cart"></i>
                  Add To Cart
                </Link>
              </div>
            </ul>
          </div>
        ))}
      </div>


  );
};

export default Wishlist;
