import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router";
import Constants from "../apis/Constants";
import Endpoints from "../apis/Endpoints";
import Navbar from "../components/Navbar";

const ProductOrderSummary=()=>{
  const {id} = useParams();
  console.log(id)
  console.log(Endpoints.PRODUCTS_URL+id)

  const [product, setProduct] = useState({})


    const getData = () => {
        axios.get(Endpoints.PRODUCTS_URL+id)
            .then((response) => {
                console.log(response.data);
                setProduct(response.data);
            })
            .catch(error => console.log(error))
    };
    useEffect(() => {
      getData()
  }, [id])
    return(
        <>
        <Navbar />
        <div className="container">
                          <div>
                          <img src={product.image} alt="" className="img-fluid" />

                        <h5>{product.title}</h5>

                        <p>{product.description}</p>
                        <h2>
                            <span>&#36;</span>{product.price}
                            <span style={{ fontsize: '22px', marginleft: '10px', color: '#888' }}>

                            </span>
                        </h2> 
                          </div>

                            <div className="ordersummarycontainer">
                        <h5>OrderSummary</h5>
                       </div>
                       </div>


        </>
    )
  }

  export default ProductOrderSummary
