import React, { useState, useEffect } from "react";
import axios from "axios";
import Constants from "../apis/Constants";
import { Link, useParams } from "react-router-dom";

import ProductList from "./Products";

const HeaderCategory = () => {
  const [categories, setCategories] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);

  const { category } = useParams();

  const getData = () => {
    axios
      .get(Constants.BASE_URL)
      .then((response) => {
        console.log(response.data);
        setCategories([...new Set(response.data.map((product) => product.category))]);
        setFilteredProducts(response.data);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    getData();
  }, []);

  useEffect(() => {
    if (category && categories.includes(category)) {
      const filtered = filteredProducts.filter((product) => product.category === category);
      setFilteredProducts(filtered);
    } else {
      // If no category is selected or the selected category is not found, show all products
      setFilteredProducts(filteredProducts);
    }
  }, [category]);

  return (
    <div>
      <ul className="nav justify-content-center">
        <Link to="/" className="list-group-item" style={{ border: "none" }}>
          All
        </Link>
        {categories.map((category, index) => (
          <Link key={index} to={`/${category}`} className="list-group-item" style={{ border: "none" }}>
            {category}
          </Link>
        ))}
      </ul>

      {/* Conditionally render the FilteredProducts component */}
      <ProductList products={filteredProducts} />
    </div>
  );
};

export default HeaderCategory;
