import { createSelector } from "reselect";

export const selectCartNumber = (state) => state.cart.numberCart;




// Select the cart state from the Redux store
const selectCartState = (state) => state.cartReducer;

// Select the cart items array from the cart state
export const selectCartItems = createSelector(
  [selectCartState],
  (cart) => cart.Carts
);


// Select the total cost of items in the cart
export const selectCartTotal = createSelector(
  [selectCartItems],
  (cartItems) => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  }
);
