import axios from "axios";
import Endpoints from "../../apis/Endpoints";
import { ActionTypes } from "../constants/action-types"

export const addToCart = (product) => {
  return {
    type: ActionTypes.ADD_TO_CART,
    payload: product
  }

}


// cart-actions.js

export const fetchProductDataForCartItems = (cartItemsWithQuantities) => {
  return (dispatch) => {
    // Fetch product details for all cart items and include their quantities
    const promises = cartItemsWithQuantities.map(({ productId, quantity }) =>
      axios.get(Endpoints.PRODUCTS_URL + productId)
    );

    Promise.all(promises)
      .then((responses) => {
        const cartItemsWithDetails = responses.map((response, index) => ({
          ...response.data,
          quantity: cartItemsWithQuantities[index].quantity,
        }));

        dispatch({
          type: ActionTypes.FETCH_PRODUCT_DATA_SUCCESS,
          payload: cartItemsWithDetails,
        });
      })
      .catch((error) => {
        console.error('Error fetching product details for cart items:', error);
      });
  };
};



export const removeFromCart = (productId) => {
  return {
    type: ActionTypes.REMOVE_FROM_CART,
    payload: productId,
  };
};

export const updateCartItemQuantity = (productId, newQuantity) => {
  return {
    type:ActionTypes.UPDATE_CART_ITEM_QUANTITY,
    payload: {
      productId,
      newQuantity,
    },
  };
};
