import React, { useContext } from "react";
import { WishlistContext } from "../components/WishlistContext";
import { Link } from "react-router-dom";

const Wishlist = ({ products }) => {
  const { wishlist } = useContext(WishlistContext);

  // Get the list of product IDs in the wishlist
  const wishlistedProductIds = Object.keys(wishlist);

  // Filter the products based on the product IDs in the wishlist
  const wishlistedProducts = products.filter((product) =>
    wishlistedProductIds.includes(product.id.toString())
  );

  return (
    <div>
      <h2>Wishlist</h2>
      {wishlistedProducts.length === 0 ? (
        <p>Your wishlist is empty.</p>
      ) : (
        <div className="row">
          {wishlistedProducts.map((product) => (
            <div className="col-sm-3" key={product.id}>
              {/* Render product details here */}
              <Link to={`/products/${product.id}`}>
              <ul className="card" style={{width:"285px",height:"450px"}}>
            <img src={product.image} className="img card-top-image" alt="..." style={{ maxWidth: "200px", maxHeight: "200px", marginTop:"20px" }} />

            <div className="wishlist">
              <FontAwesomeIcon
                icon={faHeart}
                className={`wishlist-icon ${wishlist[product.id] ? "wishlist-added" : ""}`}
                onClick={() => handleWishlistClick(product.id)}
              />
            </div>
            <div className="card-body">
              <h5 className="card-title">
                {product.title.length > 20 ? `${product.title.substring(0, 20)}...` : product.title}
              </h5>
              <div className="rating">
                {Array.from({ length: 5 }, (_, i) => (
                  <FontAwesomeIcon
                    key={i}
                    icon={faStar}
                    className={i < product.rating.rate ? "filled-star" : "empty-star"}
                  />
                ))}
                {product.rating.rate}
                <span>({product.rating.count})</span>
              </div>
              <h2>
                <span>&#36;</span>
                {product.price}
                <span style={{ fontSize: "22px", marginLeft: "10px", color: "#888" }}></span>
              </h2>

              <Link to={'/products/'+product.id} className="btn btn-primary"
              onClick={() => handleAddToCart(product.id)}>
              Add To Cart
              </Link> 
            </div>
          </ul>
              </Link>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
