import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar, faHeart } from '@fortawesome/free-solid-svg-icons';
import Constants from '../apis/Constants';
import { useParams } from 'react-router';
import axios from 'axios';

const ProductList = () => {
    const { id } = useParams();
    const [products, setProducts] = useState([]);
    const [wishlist, setWishlist] = useState({});

    const getData = () => {
        axios
            .get("https://fakestoreapi.com/products/")
            .then((response) => {
                console.log(response.data);
                setProducts(response.data); // Set the products state with the API response data
            })
            .catch(error => console.log(error));
    };

    useEffect(() => {
        getData();
    }, []);

    const handleWishlistClick = (productId) => {
        setWishlist((prevState) => ({
          ...prevState,
          [productId]: !prevState[productId] // Toggle the wishlist status for the specific product
        }));
      };

    // Save the wishlist status to local storage when the wishlist state changes
    useEffect(() => {
        localStorage.setItem('wishlist', JSON.stringify(wishlist));
    }, [wishlist]);


    const calculateAverageRating = (rating, count) => {
        if (count === 0) {
            return 0; // No ratings, return 0 as the average
        }
        return rating / count;
    };
    return (
        <div className="row">
            {products.map((product, index) => {
                const averageRating = calculateAverageRating(product.rating.rate, product.rating.count);
            return (
                <div className="col-sm-3" key={index}>
                    <ul className="card" style={{ width: "18rem" }}>
                        <img src={product.image} className="img card-top-image" alt="..." />

                        <div className="wishlist">
                            <FontAwesomeIcon
                                icon={faHeart}
                                className={`wishlist-icon ${wishlist[product.id] ? 'wishlist-added' : ''}`}
                                onClick={() => handleWishlistClick(product.id)}
                            />
                        </div>
                        <div className="card-body">
                            <h5 className="card-title">{product.title}</h5>
                            <div className="rating">
                                {Array.from({ length: 5 }, (_, i) => (
                                    <FontAwesomeIcon
                                        key={i}
                                        icon={faStar}
                                        className={i < Math.floor(averageRating) ? 'filled-star' : 'empty-star'}
                                    />
                                ))}
                                </div>
                                <h2>
                                    <span>&#8377;</span>{product.price}
                                    <span style={{ fontSize: '22px', marginLeft: '10px', color: '#888' }}>
                                    </span>
                                </h2>

                                <a href="#" className="btn btn-primary">Add To Cart</a>
                            </div>
                    </ul>
                </div>
            );
            })}
        </div>
    );
};

export default ProductList;
