import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router";
import Constants from "../apis/Constants";
import Endpoints from "../apis/Endpoints";
import Navbar from "../components/Navbar";

const ProductOrderSummary = () => {
    const { id } = useParams();
    console.log(id)
    console.log(Endpoints.PRODUCTS_URL + id)

    const [product, setProduct] = useState({})


    const getData = () => {
        axios.get(Endpoints.PRODUCTS_URL + id)
            .then((response) => {
                console.log(response.data);
                setProduct(response.data);
            })
            .catch(error => console.log(error))
    };
    useEffect(() => {
        getData()
    }, [id])
    return (
        <>
            <Navbar />
            <div className="rowordersummary" style={{ border: "solid lightgrey"}}>
                <img src={product.image} alt="" className="img-fluid" style={{ marginTop: "50px", height: "200px" }} />
                <div className="col-sm-6">

                    <h3>Brand</h3>
                    <h5>{product.title}</h5>

                    <p>{product.description}</p>
                    <h2>
                        <span>&#36;</span>{product.price}
                        <span style={{ fontsize: '22px', marginleft: '10px', color: '#888' }}>

                        </span>
                    </h2>
                </div>
                <div className="col-sm-3" style={{ borderRight: "solid lightgrey" }}>
                <h5>OrderSummary</h5>
                <ul>
                    <li>
                        <p>SubTotal:<span>&#36;</span>{product.price}`</p>

                    </li>
                </ul>
            </div>

            </div>



        </>
    )
}

export default ProductOrderSummary
