import axios from "axios";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux/es/hooks/useSelector";
import { useParams } from "react-router";
import Endpoints from "../apis/Endpoints";
import Navbar from "../components/Navbar";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import HeaderCategory from "../components/HeaderCategory";
import { selectCartItems, selectProductData } from "../Redux/selectors/cartSelectors";

const ProductOrderSummary = () => {
  const { id } = useParams();

  const cartItems = useSelector(selectCartItems);
  const productData = useSelector(selectProductData);

  return (
    <>
      <Navbar />
      <HeaderCategory />
      <div className="rowordersummary" style={{ border: "solid lightgrey" }}>
        <img
          src={productData.image}
          alt=""
          className="img-fluid"
          style={{ marginTop: "50px", height: "200px", maxWidth: "200px" }}
        />
        <div className="col-sm-7" style={{ borderRight: "solid lightgrey" }}>
          <h3>Brand</h3>
          <FontAwesomeIcon
            icon={faTrash}
            // onClick={() => handleDelete(product.id)}
            style={{
              cursor: "pointer",
              position: "absolute",
              top: "10px",
              right: "10px",
            }}
          />
          <h5 style={{ fontsize: "22px", marginleft: "10px", color: "grey" }}>
            {productData.title}
          </h5>
          <h2 style={{ fontsize: "22px", marginleft: "10px", color: "grey" }}>
            <span>&#36;</span>
            {productData.price}
          </h2>
        </div>
        <div className="col-sm-3">
          <h5>Order Summary</h5>
          <table style={{ marginTop: "20px" }}>
            <tbody>
              {cartItems.map((item) => (
                <tr key={item.id}>
                  <td>SubTotal</td>
                  <td>
                    <span>&#36;</span>
                    {item.price}
                  </td>
                </tr>
              ))}
              <tr>
                <td>ShoppingEstimate</td>
                <td>
                  <span>&#36;</span>5
                </td>
              </tr>
              <tr>
                <td>TaxEstimate</td>
                <td>
                  <span>&#36;</span>5
                </td>
              </tr>
              <tr>
                <td>
                  <h6>Order Total</h6>
                </td>
                <td>
                  <h6>
                    <span>&#36;</span>
                    {productData.price + 5 + 5}
                  </h6>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
};

export default ProductOrderSummary;
