import React, { useState,useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faStar, faHeart } from "@fortawesome/free-solid-svg-icons";

const ProductList = (...products ) => {
    const { id,title, image, price, rating } =products.data;
  const [wishlist, setWishlist] = useState({});


  const handleWishlistClick = (productId) => {
    setWishlist((prevState) => ({
      ...prevState,
      [productId]: !prevState[productId], // Toggle the wishlist status for the specific product
    }));
  };

  // Save the wishlist status to local storage when the wishlist state changes
  useEffect(() => {
    localStorage.setItem("wishlist", JSON.stringify(wishlist));
  }, [wishlist]);

  return (

     <div className="row" style={{ marginTop: "40px", marginLeft: "2em", marginRight: "2em" }}>
      {products.map((product) => (
        <div className="col-sm-3" key={id}>
          <ul className="card">
            <img src={image} className="img card-top-image" alt="..." style={{ maxWidth: "200px", maxHeight: "200px" }} />

            <div className="wishlist">
              <FontAwesomeIcon
                icon={faHeart}
                className={`wishlist-icon ${wishlist[product.id] ? "wishlist-added" : ""}`}
                onClick={() => handleWishlistClick(id)}
              />
            </div>
            <div className="card-body">
              <h5 className="card-title">
                {title.length > 20 ? `${title.substring(0, 20)}...` : title}
              </h5>
              <div className="rating">
                {Array.from({ length: 5 }, (_, i) => (
                  <FontAwesomeIcon
                    key={i}
                    icon={faStar}
                    className={i < rating.rate ? "filled-star" : "empty-star"}
                  />
                ))}
                {rating.rate}
                <span>({rating.count})</span>
              </div>
              <h2>
                <span>&#36;</span>
                {price}
                <span style={{ fontSize: "22px", marginLeft: "10px", color: "#888" }}></span>
              </h2>

              <a href="#" className="btn btn-primary">
                Add To Cart
              </a>
            </div>
          </ul>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
